import javax.swing.*; //vrear dise√±o de interfazimport java.awt.event.*; //funcionalidaades de componentespublic class Boton2 extends JFrame implements ActionListener{    JButton boton1;  public Boton2(){    setLayout(null);    boton1 = new JButton("Cerrar");    boton1.setBounds(300, 250, 100, 30);    add(boton1);    boton1.addActionListener(this);  }  public void actionPerformed(ActionEvent e){    if(e.getSource() == boton1){      System.exit(0);    }  }  public static void main(String args[]){    Boton2 formulario1 = new Boton2();    formulario1.setBounds(0, 0, 450, 350);    formulario1.setVisible(true);    formulario1.setResizable(false);    formulario1.setLocationRelativeTo(null);  }}