import javax.swing.*;import java.awt.event.*;public class Todo32 extends JFrame implements ActionListener{  private JTextField text1;  private JScrollPane scroll1;  private JTextArea area1;  private JButton boton1;    String texto = "";  public Todo32(){    setLayout(null);    text1 = new JTextField();    text1.setBounds(10, 10, 200, 30);    add(text1);    boton1 = new JButton("Agregar");    boton1.setBounds(250, 10, 100, 30);    add(boton1);    boton1.addActionListener(this);        area1 = new JTextArea();    scroll1 = new JScrollPane(area1);    scroll1.setBounds(10, 50, 400, 300);    add(scroll1);  }    public void actionPerformed(ActionEvent e){    if(e.getSource() == boton1){      texto += text1.getText() + "\n";      area1.setText(texto);      text1.setText("");    }  }    public static void main(String args[]){  Todo32 formu1 = new Todo32();  formu1.setBounds(0, 0, 540, 400);  formu1.setVisible(true);  formu1.setResizable(false);  formu1.setLocationRelativeTo(null);  }}